import React, { useRef } from 'react'
import './contact.css';
import Walmart from '../../assets/walmart.png'
import Adobe from '../../assets/adobe.png'
import Microsoft from '../../assets/microsoft.png'
import Facebook from '../../assets/facebook.png'
import FacebookIcon from '../../assets/facebook-icon.png'
import TwitterIcon from '../../assets/twitter.png'
import YoutubeIcon from '../../assets/youtube.png'
import InstagramIcon from '../../assets/instagram.png'
import emailjs from '@emailjs/browser';

const Contact = () => {
    const form = useRef();
    const sendEmail = (e) => {
        e.preventDefault();

        emailjs
          .sendForm('service_3gjfbmo', 'template_hrx56xl', form.current, {
            publicKey: 'UNnajZ81c4VxDao8_',
          })
          .then(
            () => {
              console.log('SUCCESS!');
              e.target.reset();
              alert('Email Sent!')
            },
            (error) => {
              console.log('FAILED...', error.text);
            },
          );
      };

  return (
    <section id="contactPage">
        {/* My Projects Section */}
        <div id="projects">
            <h1 className="contactPageTitle">My Projects</h1>
            <p className="clientDesc">
                Here are some standout projects that showcase my skills and creativity,
                from personal endeavors to academic collaborations.
            </p>
            <div className="worksSection">
                <h3 className="yellowText">Lorenzo's Adventure</h3>
                <p className="greySubheading">2D Platformer Game</p>
                <ul>
                    <li>Composed and co-developed using Unity & C#.</li>
                    <li><a href="https://eometheous.itch.io/lorenzos-adventure" target="_blank" rel="noopener noreferrer">Game Portfolio</a></li>
                    <li><a href="https://github.com/AustinAdair25" target="_blank" rel="noopener noreferrer">GitHub Repository</a></li>    
                </ul>
            </div>
        </div>

        {/* Contact Section */}
        <div id="contact">
            <h1 className="contactPageTitle">Contact Me</h1>
            <span className="contactDesc">Please fill out the form below to discuss any work opportunities</span>
            <form className="contactForm" ref={form} onSubmit={sendEmail}>
                <input type="text" className="name" placeholder='Your Name' name='from_name'/>
                <input type="email" className="email" placeholder='Your Email' name='from_email'/>
                <textarea className='msg' name="message" rows="5" placeholder='Your Message'></textarea>
                <button type='submit' value='Send' className="submitBtn">Submit</button>
                <div className="links">
                    <img src={FacebookIcon} alt="Facebook" className="link" />
                    <img src={TwitterIcon} alt="Twitter" className="link" />
                    <img src={YoutubeIcon} alt="Youtube" className="link" />
                    <img src={InstagramIcon} alt="Instagram" className="link" />
                </div>
            </form>
        </div>
    </section>
  )
}

export default Contact;
