import React from 'react';
import './intro.css';
import bg from '../../assets/image.png';
import btnImg from '../../assets/hireme.png'
import {Link} from 'react-scroll';

const Intro = () => {
  return (
    <section id ="intro">
        <div className="introContent">
            <span className="hello">Hello, </span>
            <span className="introText">I'm <span className="introName">Austin</span> <br />Software Developer</span>
            <p className="introPara">I am a Computer Science graduate with experience in software development, UI design, and building responsive applications. I have a strong foundation in JavaScript, React, HTML, and CSS, along with proficiency in programming languages like Java and Python. My expertise includes creating intuitive user interfaces and optimizing performance for seamless user experiences.
              <br /> software development, UI design, and building responsive, 
              <br />user-friendly applications.</p>
            <Link><button className="btn"><img src={btnImg} alt="Hire me" className='btnImg'/>Hire me</button></Link>
        </div>
        <img src={bg} alt="Profile" className="bg" />
    </section>
  )
}

export default Intro;
