import React, { useState, useEffect, useRef } from 'react';
import './intro.css';
import btnImg from '../../assets/hireme.png';
import { Link } from 'react-scroll';

const Intro = () => {
  const roles = "Software Developer";
  const [typedText, setTypedText] = useState('');
  const [index, setIndex] = useState(0);

  // Create a reference for the contact section
  const contactSection = useRef(null);

  // Function to scroll smoothly to the contact section
  const scrollToContact = () => {
    window.scrollTo({ top: contactSection.current.offsetTop, behavior: 'smooth' });
  };

  useEffect(() => {
    if (index < roles.length) {
      const timeout = setTimeout(() => {
        setTypedText((prev) => prev + roles[index]);
        setIndex(index + 1);
      }, 100); // Adjust speed of typing here
      return () => clearTimeout(timeout);
    }
  }, [index, roles]);

  return (
    <section id="intro">
      <div className="introContent">
        <span className="hello">Hello, </span>
        <span className="introText">
          I'm <span className="introName">Austin Adair</span>
          <br />
          <span className="introRole">
            {typedText}
            {/* Cursor will be inside introRole and appears after the typed text */}
            <span className="cursor" />
          </span>
        </span>
        <p className="introPara">
          I am a computer science graduate with experience in 
          <br /> software development, UI design, and building responsive, 
          <br /> user-friendly applications.
        </p>

        {/* Hire me button that scrolls to the contact section */}
        <button className="btn" onClick={scrollToContact}>
          <img src={btnImg} alt="Hire me" className="btnImg" />
          Hire me
        </button>
      </div>

      {/* Contact Section */}
      <div id="contact" ref={contactSection}>
        {/* Your contact form or details go here */}
        <h2>Contact Me</h2>
        {/* Add contact form or details here */}
      </div>
    </section>
  );
};

export default Intro;
